import './App.css';
import Users from './Users';
import { useEffect, useState } from "react";
import axios

from 'axios';
import UserList from './UserList';
const kBaseUrl = "http://localhost:5000/"


const getAllUsersApi = () => {
  return axios
    .get(`${kBaseUrl}/users`)
    .then((response) => {
      return response.data;
    })
    .catch((err) => {
      console.log(err);
    });
};

function App() {
  const [userData, setUserData] = useState([])


  const getAllUsers = () => {
    getAllUsersApi().then((users) => {
      setUserData(users)

    })
  }
  useEffect(() => {
    // data fetching code
    getAllUsers();
  }, [userData]);


  return (
    <div className="App">
      <header className="App-header">


       <Users/> 
       <UserList
       userData={userData}/>

      </header>
    </div>
  );
}

export default App;
