import { attributesToProps } from "html-react-parser";
import React from "react";
import { useState } from "react";
import './Login.css';
const kdefaultFormState = {
  email:"",
  password:"",
};

const Login = ({handleLogIn, }) => {
  const [formData, setFormData] = useState(kdefaultFormState);

  const handleChange = (event) => {
    const fieldValue = event.target.value;
    const fieldName = event.target.name
    const newFormData = {...formData, [fieldName]: fieldValue}
    setFormData(newFormData)
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    handleLogIn(formData);
    setFormData(kdefaultFormState);
  };

return (
<div className="auth-form-container">
  <h2>Login</h2>
<form className= 'login-form'onSubmit={handleSubmit}>
  <label htmlFor="email"> email </label>
  <input type="email" placeholder="youremail@gmail.com" id="email" value ="" onChange={handleChange} name="email"/>
  <p></p>
  <label htmlFor="password"> password </label>
  <input type="password" placeholder="********" id="password" value ="" onChange={handleChange} name="password"/>
  <p></p>
  <button> Log in </button>

</form>
<button className="link-btn" onClick={() => setFormData("New-User")}> Dont have an account? Create one Here.</button>
{/* <button className="link-btn" onClick={() => onFormSwitch('create user')}> Dont have an account? Create one Here.</button> */}

</div>
)


};

export default Login
