const kBaseUrl = "http://localhost:5000/"

const directionsAPI = (data) => {
  const currentData = {...data};
  return axios 
  .get(`${kBaseUrl}/maps`, currentData)
  .then((response) => {
    return response.data;
  })
  .catch((err) => {
    console.log(err)
  })

}

const getDirections = (address) => {


 directionsAPI(address)
